import bodyParser from 'body-parser'
import cors from 'cors'
import express from 'express'
import helmet from 'helmet'
import morgan from 'morgan'

import config from '@src/configs/app.config'
import i18n from '@src/libs/i18n'
import routes from '@src/rest-resources/routes'
import { errorHandlerMiddleware } from './middlewares/errorHandler.middleware'

const app = express()

app.use(helmet())

// app.use(bodyParser.json())
app.use(
  bodyParser.json({
    verify: (req, res, buf) => {
      req.rawBody = buf.toString(); // Save raw body as string
    },
  })
)

app.use(bodyParser.json())

app.use(morgan('tiny'))

app.use(i18n.init)

// CORS Configuration
const corsOptions = {
  credentials: true,
  origin: function (origin, callback) {
    // Allow requests with no origin (like mobile apps or curl requests)
    if (!origin) return callback(null, true);

    const allowedOrigins = config.get('app.origin').split(',').filter(origin => origin.trim());

    // Add common development origins
    const developmentOrigins = [
      'http://localhost:3000',
      'http://localhost:5173',
      'http://127.0.0.1:3000',
      'http://127.0.0.1:5173',
      'https://dinero-sweeps.vercel.app'
    ];

    const allAllowedOrigins = [...allowedOrigins, ...developmentOrigins];

    if (allAllowedOrigins.includes(origin) || allAllowedOrigins.includes('*')) {
      callback(null, true);
    } else {
      console.log('CORS blocked origin:', origin);
      callback(new Error('Not allowed by CORS'));
    }
  },
  methods: ['GET', 'POST', 'PUT', 'PATCH', 'DELETE', 'OPTIONS'],
  allowedHeaders: ['Content-Type', 'Authorization', 'X-Requested-With', 'Accept'],
  credentials: true
}

app.use(cors(corsOptions))


app.use(routes)

app.use(async (req, res) => {
  res.status(404).json({ message: 'Welcome to player backend' })
})

app.use(errorHandlerMiddleware)

export default app
