import React from "react";

const CategoryTabs = ({ categories, selected, onSelect }) => (
  <div className="flex gap-2 mb-4 flex-wrap">
    {categories.map((cat) => (
      <button
        key={cat}
        className={`px-4 py-1 rounded-full font-semibold text-sm transition-all duration-150
          ${selected === cat ? "bg-yellow-400 text-black shadow-glow" : "bg-gray-800 text-white hover:bg-yellow-400 hover:text-black"}`}
        onClick={() => onSelect(cat)}
      >
        {cat}
      </button>
    ))}
  </div>
);

export default CategoryTabs;
