import React from "react";

const GameCard = ({ image, title, category }) => (
  <div className="bg-gradient-to-b from-gray-900 to-black rounded-xl shadow-lg p-2 flex flex-col items-center hover:scale-105 transition-transform duration-200 cursor-pointer">
    <img src={image} alt={title} className="w-28 h-28 object-cover rounded-lg mb-2" />
    <div className="text-white font-bold text-sm text-center mb-1">{title}</div>
    <div className="text-xs text-yellow-400 font-semibold uppercase tracking-wide">{category}</div>
  </div>
);

export default GameCard;
