import React from "react";
import GameCard from "./GameCard";

const GameGrid = ({ games }) => (
  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
    {games.map((game, idx) => (
      <GameCard key={idx} {...game} />
    ))}
  </div>
);

export default GameGrid;
