import React from "react";

const categories = ["Promotions", "Gameplay", "Gameway", "Net Gaming", "Tada Gaming", "Hacksaw gaming"];
const slots = [
  { title: "Gold Party", img: "https://dummyimage.com/120x160/ffd700/000&text=Gold+Party" },
  { title: "Pink Joker", img: "https://dummyimage.com/120x160/ff69b4/000&text=Pink+Joker" },
  { title: "3x Catch", img: "https://dummyimage.com/120x160/00ffff/000&text=3x+Catch" },
  { title: "Royal Express", img: "https://dummyimage.com/120x160/ffb347/000&text=Royal+Express" },
  { title: "Jackpot White Rhino", img: "https://dummyimage.com/120x160/fff/000&text=Jackpot+White+Rhino" },
];

const GameList = () => (
  <main className="flex-grow-1 p-4" style={{ background: "#000", minHeight: "100vh" }}>
    <div className="mb-4 d-flex gap-3 align-items-center">
      <img src="https://dummyimage.com/600x120/222/fff&text=Banner+1" alt="Banner" className="rounded" style={{ maxWidth: 600, width: "100%" }} />
      <img src="https://dummyimage.com/300x120/333/fff&text=Banner+2" alt="Banner" className="rounded" style={{ maxWidth: 300, width: "100%" }} />
    </div>
    <div className="mb-3 d-flex gap-2 align-items-center">
      {categories.map((cat) => (
        <button key={cat} className="btn btn-sm btn-outline-light rounded-pill px-3">{cat}</button>
      ))}
    </div>
    <div className="mb-4">
      <h5 className="text-white mb-3">Slots</h5>
      <div className="row g-3">
        {slots.map((game, idx) => (
          <div className="col-6 col-md-3 col-lg-2" key={idx}>
            <div className="card bg-dark text-white border-0">
              <img src={game.img} alt={game.title} className="card-img-top rounded" />
              <div className="card-body p-2">
                <div className="card-title small fw-bold text-center">{game.title}</div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
    {/* Add more sections for Scratchcards, Keno, etc. as needed */}
  </main>
);

export default GameList;
